import React, { useState, useEffect } from 'react';
import { Form} from 'react-bootstrap';
const Calculator = () => {
  const [segundos, setSegundos] = useState(0);
  const [minutos, setMinutos] = useState(0);
  const [hora, setHora] = useState(0);
  const [cronometroAtivo, setCronometroAtivo] = useState(false);
  const [descrever, setDescrever] = useState(true);
  const [iniciar, setIniciar] = useState(false);
  const [mostrarIniciar, setMostrarIniciar] = useState(false);

  useEffect(() => {
    if(segundos === 0 && iniciar){
        setMinutos(minutos => minutos - 1)
        setSegundos(60)
    }
  }, [segundos, iniciar]);

  useEffect(() => {
  if(minutos === 0 && iniciar){
    setHora(hora => hora - 1)
    setSegundos(60)
    setMinutos(60)
}

}, [minutos, iniciar]);


  useEffect(() => {
    let intervalo;
    if (cronometroAtivo && iniciar) {
      intervalo = setInterval(() => {
        setSegundos(segundos => segundos - 1);
      }, 1000);
    } else {
      clearInterval(intervalo);
    }
    return () => clearInterval(intervalo);
  }, [cronometroAtivo, iniciar]);

  const iniciarCronometro = () => {
    setDescrever(false)
    setIniciar(true)
    setCronometroAtivo(true);
  };

  const pausarCronometro = () => {
    setIniciar(false)
    setCronometroAtivo(false);
  };

  const reiniciarCronometro = () => {
    setSegundos(0);
    setMinutos(0)
    setHora(0)
    setIniciar(false)
    setCronometroAtivo(false);
    setDescrever(true)
  };

  return (
    <div>

{descrever &&
     <p>Informe os segundos</p>
}
{descrever &&
   
                <Form.Control
                    onChange={e => { setSegundos(e.target.value); setMostrarIniciar(true) }}
                    value={segundos}
                />
            }
            {descrever &&
     <p>Informe os minutos</p>
}
            {descrever &&
                <Form.Control
                    onChange={e => { setMinutos(e.target.value); setMostrarIniciar(true)}}
                    value={minutos}
                />
            }
             {descrever &&
     <p>Informe as horas</p>
}
             {descrever &&
                <Form.Control
                    onChange={e => { setHora(e.target.value); setMostrarIniciar(true) }}
                    value={hora}
                />
            }


      <h1>Cron√¥metro</h1>
      <p>{segundos} Segundos</p>
      <p>{minutos} Minutos</p>
      <p>{hora} Horas</p>
      <div>
      {mostrarIniciar &&
        <button onClick={iniciarCronometro}>Iniciar</button>
      }
        
        <button onClick={pausarCronometro}>Pausar</button>
        <button onClick={reiniciarCronometro}>Reiniciar</button>
      </div>
    </div>
  );
};

export default Calculator;
