                            <Form.Control
                                name="CIFRAO_VALOR_MIN"
                                className="filtros-cifrao campo-texto-cor-3"
                                type="text"
                                value={placar26}
                                placeholder="Placar"
                                onChange={e => setPlacar26(e.target.value)}
                            />
                            <Form.Control
                                name="VALOR_MIN"
                                className="filtros-iccid-valor-min campo-texto-cor-3"
                                type="text"
                                maxLength="4"
                                value={time2f}
                                // onChange={e => validarNumero("campo-texto-valor-minimo", e.target.value)}
                                placeholder="Time"
                            />
                            <label className="fonte-cor-1 campo-texto-cor-3 break-3"></label>
                            <Button className="btn-filtro-arqui" onClick={(e) => cadastrarJogador()}>
                                <div>Enviar</div>
                            </Button>
                        </div>

                    </div>
                </div>

            </Form.Group>
            <Form.Group>
                <div className="filtros-texto-linha-margin">
                    <div className="col-sm">
                        <div className="d-flex">
                            <Form.Control
                                name="CIFRAO_VALOR_MIN"
                                className="filtros-cifrao campo-texto-cor-3"
                                type="text"
                                value={placar27}
                                placeholder="Placar"
                                onChange={e => setPlacar27(e.target.value)}
                            />
                            {/* <div className="x">X</div> */}
                            <Form.Control
                                name="VALOR_MIN"
                                className="filtros-iccid-valor-min campo-texto-cor-3"
                                type="text"
                                maxLength="4"
                                value={time2f}
                                // onChange={e => validarNumero("campo-texto-valor-minimo", e.target.value)}
                                placeholder="Time"
                            />

                            <label className="fonte-cor-1 campo-texto-cor-3 break-3"></label>

                            <Form.Control
                                name="CIFRAO_VALOR_MIN"
                                className="filtros-cifrao campo-texto-cor-3"
                                type="text"
                                value={placar28}
                                placeholder="Placar"
                                onChange={e => setPlacar28(e.target.value)}
                            />
                            <Form.Control
                                name="VALOR_MIN"
                                className="filtros-iccid-valor-min campo-texto-cor-3"
                                type="text"
                                maxLength="4"
                                value={time1f}
                                // onChange={e => validarNumero("campo-texto-valor-minimo", e.target.value)}
                                placeholder="Time"
                            />
                            <label className="fonte-cor-1 campo-texto-cor-3 break-3"></label>
                            <Button className="btn-filtro-arqui" onClick={(e) => cadastrarJogador()}>
                                <div>Enviar</div>
                            </Button>

                        </div>

                    </div>

                </div>

            </Form.Group>

            <h3>Time 2</h3>
            <Form.Control
                onChange={e => { setTime2(e.target.value) }}
                value={time2}
            />
            <h3>Time 3</h3>
            <Form.Control
                onChange={e => { setTime3(e.target.value) }}
                value={time3}
            />
            <h3>Time 4</h3>
            <Form.Control
                onChange={e => { setTime4(e.target.value) }}
                value={time4}
            />
            <Button className="btn-filtro-arquivo" onClick={(e) => cadastrarTimes()}>
                <div>Enviar</div>
            </Button>


            <h1>Cadastre os jogadores abaixo</h1>
            <h3>Time</h3>
            <Form.Control
                onChange={e => { setTime(e.target.value) }}
                value={time}
            />
            <h3>Jogador 1</h3>
            <Form.Control
                onChange={e => { setJogador1(e.target.value) }}
                value={jogador1}
            />
            <h3>Jogador 2</h3>
            <Form.Control
                onChange={e => { setJogador2(e.target.value) }}
                value={jogador2}
            />
            <h3>Jogador 3</h3>
            <Form.Control
                onChange={e => { setJogador3(e.target.value) }}
                value={jogador3}
            />
            <h3>Jogador 4</h3>
            <Form.Control
                onChange={e => { setJogador4(e.target.value) }}
                value={jogador4}
            />
            <Button className="btn-filtro-arquivo" onClick={(e) => cadastrarJogador()}>
                <div>Enviar</div>
            </Button>



            <div className="ladoC">


                <h1>Pesquise aqui o time</h1>
                <Form.Control
                    onChange={e => { setPesquisarTime(e.target.value) }}
                    value={pesquisarTime}
                />
                <Button className="btn-filtro-arquivo" onClick={(e) => pesquisaTime()}>
                    <div>Pesquisar</div>
                </Button>

                {resultado &&
                    <div>
                        <h3>Nenhum resultado encontrado</h3>
                    </div>

                }

                {mostrarTime &&
                    <div>
                        <BootstrapTable
                            hover={true}
                            classes="tabela"
                            condensed={true}
                            keyField='id_time'
                            data={itens}
                            columns={colunasTimes}
                            bootstrap4={true}
                            bordered={false}
                        />
                    </div>

                }

            </div>
        </>

    )
}
