// reducers/exampleReducer.js
import { IDIOMA } from './actions';
import React, { useState } from 'react';
import en from '../idioma/ingles.json';
import pt from '../idioma/portugues.json';
const [language, setLanguage] = useState('en');

const initialState = {
  idioma: language === 'en' ? en : pt
};

const reduxH = (state = initialState, action) => {
  switch (action.type) {
    case IDIOMA:
      return {
        ...state,
        exampleData: action.payload,
      };
    default:
      return state;
  }
};

export default reduxH;
