// PROJETO PARA DESAFIO DE TESTE

import React, { useState, useEffect } from 'react';/*eslint-disable*/
import '../../css/login/login.css';
import { Button, Image, Form, InputGroup, FormControl, Col, Carousel } from 'react-bootstrap';
import { apiC } from "../../conexoes/api";
import BootstrapTable from 'react-bootstrap-table-next';
import { useNavigate } from 'react-router-dom';
import SelectGrupo from '../login/selectGrupo'
import { useSelector, useDispatch } from 'react-redux';
import { exampleAction, idioma } from '../../actions/actions';
import en from '../../idioma/ingles.json';
import pt from '../../idioma/portugues.json';
import es from '../../idioma/espanhou.json';
import '../../css/login/styles.scss'; // Importe seu arquivo SCSS aqui


export default function TelaLogin() {

    //VARIAVEIS
    const [mostrarRecuperarSenha, setMostrarRecuperarSenha] = useState('padrao');
    const [loginError, setLoginError] = useState(false);
    const [email, setEmail] = useState('');
    const [emailCadastro, setEmailCadastro] = useState('');
    const [password, setPassword] = useState('');
    const [passwordCadastro, setPasswordCadastro] = useState('');
    const [mostrarEsconder, setMostrarEsconder] = useState("password");
    const [mostrarUploadCsv, setMostrarUploadCsv] = useState(false);
    const [carregando, setCarregando] = useState(false);
    const [erroAoCadastrar, setErroAoCadastrar] = useState(false);
    const [nomeArquivo, setNomeArquivo] = useState('');
    const [arquivo, setArquivo] = useState('');
    const [mostrarTabela, setMostrarTabela] = useState(false);
    const [itens, setItens] = useState([]);
    const [contMen, setContMen] = useState([]);
    const [contFem, setContFem] = useState([]);
    const [medIdade, setMedIdade] = useState([]);
    const [text, setText] = useState('');
    // const text = useSelector(state => state.reduxH.idioma);
    const dispatch = useDispatch();


    const [filtroGrupo, setFiltroGrupo] = useState('Selecione')
    const [resetGrupo, setResetGrupo] = useState(false)
    const [cdGrupo, setCdGrupo] = useState('')
    const [revelarSelectGrupo, setRevelarSelectGrupo] = useState(false)
    const [idIdioma, setIdIdioma] = useState('')

    

    let totalItens = 0
    let contador = 0
    let itensVar = []
    let contadorMale = 0
    let contadorFemale = 0


    const navigate = useNavigate();
    async function paraNavegar() {
        window.location.href = '/cadastroUsuario';
    }

    async function salvarToken(response) {
        localStorage.setItem("keyToken", JSON.stringify(response.data.token))

    }

    useEffect(() => {
        localStorage.setItem('idioma', JSON.stringify(pt));
        setText(JSON.parse(localStorage.getItem('idioma')))
    }, [])


    useEffect(() => {
        async function mudarIdioma() {

            if (idIdioma == 2) {
                localStorage.setItem('idioma', JSON.stringify(en));
                setText(JSON.parse(localStorage.getItem('idioma')))
            }else if(idIdioma == 1){
                localStorage.setItem('idioma', JSON.stringify(pt));
                setText(JSON.parse(localStorage.getItem('idioma')))
            }else if(idIdioma == 3){
                localStorage.setItem('idioma', JSON.stringify(es));
                setText(JSON.parse(localStorage.getItem('idioma')))
            }
            
        }

      

        mudarIdioma()
    }, [cdGrupo])


    //FUNÇÃO DE ENFETUAR O LOGIN
    async function efetuarLogin() {
        setLoginError("");
        setCarregando(true)
        if (!email || !password) {
            setLoginError("Preencha e-mail e senha!");
        } else {
            setCarregando(true)
            await apiC.post('usuarios/logar', {
                usuario: email,
                senha: password
            })
                .then(async function (response) {
                    await salvarToken(response)
                    await paraNavegar()
                })
                .catch(function (error) {
                    // MENSAGENS DE ERRO APARECERAM CASO LOGIN DER ALGUM ERRO
                    setCarregando(false)
                    setLoginError(true)
                });
        }
    }

    console.clear();

    var s = Snap();
    var sMaxX = 800;
    var sMaxY = 600;
    var viewBoxList = [0, 0, sMaxX, sMaxY];
    s.attr({
        viewBox: viewBoxList
    });
    
    var gLines = s.g();
    var gText = s.g();
    var patt;
    var maskObj;
    var textt;
    var maskElem = s.mask();
    
    var pSize = 700;
    var maxLines = 16;
    var maxLinesDouble = maxLines * 2;
    var lineStep = pSize / maxLines;
    var lines = [];
    var pathDur = 1000;
    var delay = 250;
    
    var colorSteps = maxLines / 2;
    var colors = ['purple',
                  'crimson',
                  'orangered',
                  'orange',
                  'gold',
                  'yellowgreen',
                  'steelblue',
                  'teal',
                  'purple'
                 ]
    
    var lineLength = Math.sqrt( Math.pow( pSize, 2 ) * 2);
    
    
    var lineObj = function () {
        var d = 'M' + [pSize, 0, 0, pSize];
        var path = s.path(d);
        var pos = 0;
        var addMask = false;
        var pathDelay = 0;
        var dashArray = 0;
        var strokeW = 0;
        
        this.init = function ( params ) {
            pos = params.pos;
            strokeW = params.strokeW;
            var strokeColor = params.color || 'hotpink'
            var offsetX = params.offsetX || 0;
            var x = pSize - lineStep * (pos + .5) + offsetX;
            var translateParams = [x, 0];
            
            pathDelay = params.delay || delay;
            dashArray = lineLength;
            addMask = params.addMask || false;
    
            path.attr({
                transform: 'translate(' + translateParams + ')',
                'stroke-width': strokeW,
                stroke: strokeColor,
                'stroke-linecap': 'square',
                'stroke-dashoffset': lineLength,
                'stroke-dasharray': dashArray
            });
            
            gLines.add(path);
            
        }// Init
        
        this.reset = function () {
            path.attr({
                'stroke-dashoffset': lineLength,
                'stroke-dasharray': dashArray
            });
        }
        
        this.animdDelay = function() {
            setTimeout(pathAnim, 
                       (maxLinesDouble - pos) * pathDelay
                      );
        }
        
        var countNextAnim = 0;
        
        function runNextAnim() {
            if ( addMask == true ) {
                // Why 0?
                if ( pos == 0) {
                    countNextAnim++;
                    
                    maskObj.maskAnim();
                }
            }
        }
        
        function pathAnim () {
    
            path.animate({
                'stroke-dashoffset': '0'
                },
                pathDur,
                runNextAnim
                );
        }
        
    }// lineObj
    
    // ------------------------------------
    
    function createLines( params ) {
        
        var strokeW = params.strokeW;
        
        for ( var i = 0; i < maxLinesDouble; i++ ) {
            var line = new lineObj;
            var color = params.color || colors[i % colorSteps];
            
            line.init({
                pos: i,
                strokeW: params.strokeW,
                offsetX: params.offsetX,
                delay: params.delay,
                addMask: params.addMask || false,
                color: color,
            });
            
            lines.push(line);
        }
    }
    
    // ------------------------------------
    
    function createPattern() {
        
    //     console.log('* - createPattern');
        
        var rect = s.rect(0,0, pSize, pSize);
        rect.attr({
            fill: 'white',
        });
        
        gLines.add(rect);
        
        createLines({
            strokeW: lineStep / 1.4,
            addMask: false
            });
        
        createLines({
            strokeW: 2,
            color: '#002',
            offsetX: lineStep / 2 + 7,
            delay: 300,
            addMask: true
            });
        
          patt = gLines.toPattern(0,0, pSize, pSize);    
        
    }
    
    function animatePattern() {
      
        for ( var i = 0; i < lines.length; i++ ) {
            var line = lines[i];
            
            line.reset();
            line.animdDelay();
        }
    }
    
    // ------------------------------------
    
    var textObj = function () {
        
        var textDur = 1500;
        var dashoffset = 1200;
        var textGInit = s.g();
            var text1 = s.text('50%','34%','Hello');
            var text2 = s.text('50%','73%','world');
    
        text1.attr({
           dy: '.3em',
           'font-size': '1.15em'
        });
        text2.attr({
           dy: '.3em'
        });
        
        textGInit.add(text1, text2);
    
        textGInit.attr({
            'text-anchor': 'middle',
            'font': '15em/1 Impact',
            fill: 'white',
            stroke: '#000',
            'stroke-width': 3,
            'stroke-dasharray': dashoffset,
            'stroke-dashoffset': dashoffset
        });
    
        var textGFill = textGInit.clone();
    
        textGInit.attr({
            transform: 'translate(10,10)'
        });
        
        gText.add(textGInit, textGFill);
        
        this.textAnim = function () {
            textGFill.animate({
                'stroke-dashoffset': 0
                },
                textDur,
                setTextStroke);
        }
        
        function setTextStroke () {
            setTextFill();
            
            textGInit.animate({
                'stroke-dashoffset': 0
                },
                textDur
                );
        }
        
        function setTextFill () {
            
            animatePattern();
            
            textGFill.attr({
                fill: patt
            });
        }
        
        this.reset = function () {
            
            var initState = {
                fill: 'white',
                'stroke-dasharray': dashoffset,
                'stroke-dashoffset': dashoffset
            };
            
            textGInit.attr( initState );
            textGFill.attr( initState );
            
            this.textAnim();
        }
    }
    
    // ------------------------------------
    
    function createText() {
    //     console.log('* - createText');
        textt = new textObj;
        textt.textAnim();
    }
    
    // ------------------------------------
    
    var maskObjInit = function () {
        var maskShape;
        
        var currentStep = 0;
        var steps = [
            {rx: '10%', ry: "10%"},
            {rx: '35%', ry: "35%"},
            {rx: '0%', ry: "0%"}
        ];
        
        this.init = function () {
            maskShape = s.ellipse('50%', '50%', '100%', '100%');
        
            maskShape.attr({
                fill: "white"
            });
    
            maskElem.add(maskShape);
    
            gText.attr({
                mask: maskElem
            });
        }
        
        this.maskAnim = function () {
    //         console.log('- * - anim mask');
            
            if ( currentStep == steps.length ) {
                setTimeout(reRun, 1000);
                return;
            }
            
            maskShape.animate(
                steps[currentStep]
                ,
                300,
                maskObj.maskAnim);
            currentStep++;
        }
        
        this.reset = function () {
            currentStep = 0;
            
            var initState = {
                rx: '100%', 
                ry: "100%"
            };
            
            maskShape.attr(initState);
        }
    }
    
    function createMask() {
    //     console.log('* - createMask');
        maskObj = new maskObjInit;
        maskObj.init();
    }
    
    // ------------------------------------
    
    createPattern();
    createText();
    createMask();
    
    function reRun() {
        
        maskObj.reset();
        text.reset();
    }
    
    /*var wrapper = document.createElement("div");
    document.body.appendChild(wrapper);
    wrapper.appendChild(s.node);*/



    return (
        <>
            {carregando &&
                <h1>carregando..</h1>
            }
            {erroAoCadastrar &&
                <h4>Erro ao cadastrar</h4>
            }
            {loginError &&
                <h4> E-mail inválido ou senha incorreta </h4>
            }



            {mostrarRecuperarSenha === 'padrao' &&
                <>
                    <div>
                        <div className="email">
                            <Form.Control

                                type="email"
                                onChange={e => setEmail(e.target.value)}
                                value={email}
                                name="campoEmail"
                                id="campoEmail"
                                placeholder={text.email}
                                className="label-login"
                            />
                        </div>
                        <div className='espaco-11'></div>
                        <div>
                            <div className='senha'>
                                <FormControl

                                    placeholder={text.senha}
                                    className="label-login"
                                    type={mostrarEsconder}
                                    onChange={e => setPassword(e.target.value)}
                                    value={password}
                                    name="campoSenha"
                                    id="campoSenha"
                                    maxLength="20"
                                />
                            </div>

                        </div>

                        <div>

                            <div className='senha'>
                                <div className="espaco-11" ></div>

                                <div className="coluna-alterar-ciclo-vida-1-status-grupo">
                                    <label className='idioma'>{text.idioma}</label>
                                    <div className="break-4"></div>
                                    <Form.Control placeholder="Idioma" name="GRUPO" type="text" value={filtroGrupo == 'Selecione' || resetGrupo ? '' : filtroGrupo} className="esconder label-login" onChange={(e) => setCdGrupo(e.target.value)} />
                                    {revelarSelectGrupo === false &&
                                        <Button disabled={revelarSelectGrupo !== '' && revelarSelectGrupo !== null && revelarSelectGrupo !== undefined ? false : true} className="campo-texto-cor-3 campo-select-filtro-7" onClick={() => setRevelarSelectGrupo(!revelarSelectGrupo)}>
                                            <div className="fonte-cor-1 label-campo campo-texto-select-tamanho-2">{resetGrupo ? "Selecione" : filtroGrupo.split('*')[0]}</div>
                                            <img className={revelarSelectGrupo ? "campo-select-icone-ativado nao-selecionavel" : "campo-select-icone-desativado-filtro-b nao-selecionavel"} />
                                        </Button>
                                    }
                                    {revelarSelectGrupo === true &&
                                        <SelectGrupo
                                            rota={"filtroSimcard"}
                                            setRevelarSelectGrupo={setRevelarSelectGrupo}
                                            setFiltroGrupo={setFiltroGrupo}
                                            setCdGrupo={setCdGrupo}
                                            setResetGrupo={setResetGrupo}
                                            nomeGrupo={filtroGrupo}
                                            setIdIdioma={setIdIdioma}
                                        >
                                        </SelectGrupo>
                                    }
                                </div>

                            </div>

                        </div>
                    </div>

                    <div className='senha'>
                        <Button
                            onClick={(e) => efetuarLogin()}
                            className='botao-entrar'
                        >{text.entrar}

                        </Button>
                    </div>
                    <div className="espaco-11" ></div>
                    <div className="campos-texto-login">
                        <div className='cadastrar'>
                            <Button onClick={(e) => navigate('/cadstrousuario')}
                                className='botao-cadastrar'
                            >{text.cadastrar}</Button>
                        </div>
                    </div>



                </>

            }
        </>

    )
}
