// reducers/exampleReducer.js
import { IDIOMA } from './actions';

const initialState = {
  idioma: false,
};

const idioma = (state = initialState, action) => {
  switch (action.type) {
    case IDIOMA:
      return {
        ...state,
        exampleData: action.payload,
      };
    default:
      return state;
  }
};

export default exampleReducer;
