import React, { useState, useEffect } from 'react';

const Calculator = () => {
  const [segundos, setSegundos] = useState(60);
  const [minutos, setMinutos] = useState(5);
  const [hora, setHora] = useState(0);
  const [cronometroAtivo, setCronometroAtivo] = useState(false);
  const [descrever, setDescrever] = useState(true);

  useEffect(() => {
    if(segundos === 0){
        setMinutos(minutos => minutos - 1)
        setSegundos(60)
    }
  }, [segundos]);

  useEffect(() => {
  if(minutos === 0){
    setHora(hora => hora - 1)
    setSegundos(60)
    setMinutos(5)
}

}, [minutos]);


  useEffect(() => {
    let intervalo;
    if (cronometroAtivo) {
      intervalo = setInterval(() => {
        setSegundos(segundos => segundos - 1);
      }, 1000);
    } else {
      clearInterval(intervalo);
    }
    return () => clearInterval(intervalo);
  }, [cronometroAtivo]);

  const iniciarCronometro = () => {
    setDescrever(false)
    setCronometroAtivo(true);
  };

  const pausarCronometro = () => {
    setCronometroAtivo(false);
  };

  const reiniciarCronometro = () => {
    setSegundos(60);
    setMinutos(5)
    setHora(0)
    setCronometroAtivo(false);
  };

  return (
    <div>

{descrever &&
     <p>Informe os segundos</p>
}
{descrever &&
   
                <Form.Control
                    onChange={e => { setSegundos(e.target.value) }}
                    value={segundos}
                />
            }
            {descrever &&
     <p>Informe os minutos</p>
}
            {descrever &&
                <Form.Control
                    onChange={e => { setMinutos(e.target.value) }}
                    value={minutos}
                />
            }
             {descrever &&
     <p>Informe as horas</p>
}
             {descrever &&
                <Form.Control
                    onChange={e => { setHora(e.target.value) }}
                    value={hora}
                />
            }

      <h1>Cron√¥metro</h1>
      <p>{segundos} Segundos</p>
      <p>{minutos} Minutos</p>
      <p>{hora} Horas</p>
      <div>
        <button onClick={iniciarCronometro}>Iniciar</button>
        <button onClick={pausarCronometro}>Pausar</button>
        <button onClick={reiniciarCronometro}>Reiniciar</button>
      </div>
    </div>
  );
};

export default Calculator;
