// reducers/exampleReducer.js
import { IDIOMA } from './actions';
import React, { useState } from 'react';
import en from '../idioma/ingles.json';
import pt from '../idioma/portugues.json';

const initialState = {
  idioma: 'en' ? en : pt,
};


export const reduxH = (state = ESTADO_INICIAL, action) => {
    if (action.type === 'IDIOMA') {
        return {
            ...state, 
            idioma: action.payload
        }
    }
    else if (action.type === 'EDITAR_REGRA_AUTOMACAO') {
        return {
            ...state, 
            revelarEditarRegra: action.parametro
        }
    }
    else {
        return state;
    }
};
