    async function verificarQuemAvanca() {
        await apiC.post("copa/bucartodos")
            .then(response => {
                if (response.status === 200) {

                    let v1 = response.data.result[0].placar + response.data.result[3].placar
                    let v2 = response.data.result[1].placar + response.data.result[2].placar
                    let v3 = response.data.result[4].placar + response.data.result[7].placar
                    let v4 = response.data.result[5].placar + response.data.result[6].placar
                    let v5 = response.data.result[8].placar + response.data.result[11].placar
                    let v6 = response.data.result[9].placar + response.data.result[10].placar
                    let v7 = response.data.result[12].placar + response.data.result[15].placar
                    let v8 = response.data.result[13].placar + response.data.result[14].placar
                    let v9 = response.data.result[16].placar + response.data.result[19].placar
                    let v10 = response.data.result[18].placar + response.data.result[17].placar
                    let v11 = response.data.result[20].placar + response.data.result[23].placar
                    let v12 = response.data.result[21].placar + response.data.result[22].placar
                    let v13 = response.data.result[24].placar + response.data.result[27].placar
                    let v14 = response.data.result[25].placar + response.data.result[26].placar


                    if (v1 > v2) {
                        if (v3 > v4) {
                            if (v2 < v4) {
                                setTime2s(response.data.result[0].nome)
                            }else{
                                setTime1s(response.data.result[0].nome)
                            }
                        }else {
                            if (v2 < v3) {
                                setTime2s(response.data.result[0].nome)
                            }else{
                                setTime1s(response.data.result[0].nome)
                            }    
                        }
                    } else {
                        if (v3 > v4) {
                            if (v1 < v4) {
                                setTime2s(response.data.result[1].nome)
                            }else{
                                setTime1s(response.data.result[1].nome)
                            }
                        }else {
                            if (v1 < v3) {
                                setTime2s(response.data.result[1].nome)
                            }else{
                                setTime1s(response.data.result[1].nome)
                            }    
                        }

                    }
                    if(v3>v4){if(v1>v2){if(v4<v2){setTime2s(response.data.result[2].nome)}else{setTime1s(response.data.result[2].nome)}}else{if(v4<v1){setTime2s(response.data.result[2].nome)}else{setTime1s(response.data.result[2].nome)}}}else{if(v1>v2){if(v3<v2){setTime2s(response.data.result[3].nome)}else{setTime1s(response.data.result[3].nome)}}else{if(v3<v1){setTime2s(response.data.result[3].nome)}else{setTime1s(response.data.result[3].nome)}}}
                    if(v5>v6){if(v7>v8){if(v6<v8){setTime4s(response.data.result[4].nome)}else{setTime3s(response.data.result[4].nome)}}else{if(v6<v7){setTime4s(response.data.result[4].nome)}else{setTime3s(response.data.result[4].nome)}}}else{if(v7>v8){if(v5<v8){setTime4s(response.data.result[5].nome)}else{setTime3s(response.data.result[5].nome)}}else{if(v5<v7){setTime4s(response.data.result[5].nome)}else{setTime3s(response.data.result[5].nome)}}}
                    if(v7>v8){if(v5>v6){if(v8<v6){setTime4s(response.data.result[6].nome)}else{setTime3s(response.data.result[6].nome)}}else{if(v8<v5){setTime4s(response.data.result[6].nome)}else{setTime3s(response.data.result[6].nome)}}}else{if(v5>v6){if(v7<v6){setTime4s(response.data.result[7].nome)}else{setTime3s(response.data.result[7].nome)}}else{if(v7<v5){setTime4s(response.data.result[7].nome)}else{setTime3s(response.data.result[7].nome)}}}
                    
                    
                    if(v9>v10){if(v11>v12){if(v10<v12){setTime2f(response.data.result[8].nome)}else{setTime1f(response.data.result[8].nome)}}else{if(v10<v11){setTime2f(response.data.result[8].nome)}else{setTime1f(response.data.result[8].nome)}}}else{if(v11>v12){if(v9<v12){setTime2f(response.data.result[9].nome)}else{setTime1f(response.data.result[9].nome)}}else{if(v9<v11){setTime2f(response.data.result[9].nome)}else{setTime1f(response.data.result[9].nome)}}}
                    if(v11>v12){if(v13>v14){if(v12<v14){setTime2f(response.data.result[10].nome)}else{setTime1f(response.data.result[10].nome)}}else{if(v12<v13){setTime2f(response.data.result[10].nome)}else{setTime1f(response.data.result[10].nome)}}}else{if(v13>v14){if(v11<v14){setTime2f(response.data.result[11].nome)}else{setTime1f(response.data.result[11].nome)}}else{if(v11<v13){setTime2f(response.data.result[11].nome)}else{setTime1f(response.data.result[11].nome)}}}

                }
            })
            .catch((error) => {
                alert(error.response.data)

            });
    }





    function inserirData(data) {
        itensVar = []
        for (let i = 0; i < data.length; i++) {
            if (contador == i) {
                let k = i
                for (let j = 0; j < data.length; j++) {
                    itensVar[k] = data[j]
                    k++
                }
            }
            setItens(JSON.parse(JSON.stringify(itensVar)))
        }
    }

    async function cadastrarNovoTime() {
        let enviarBanco = []
        enviarBanco.push(novoTime1, novoTime2, novoTime3, novoTime4, novoTime5, novoTime6, novoTime7, novoTime8)
        for (let i = 0; i < enviarBanco.length; i++) {
            await apiC.post("copa/cadastrarTime", {
                "nome": enviarBanco[i],
            }).then(response => {
                if (response.status === 200) {
                    if (contador === i) {
                        let k = i
                        for (let j = 0; j < response.data.length; j++) {
                            itensVar[k] = response.data[j]
                            k++
                        }
                    }
                    setItens(JSON.parse(JSON.stringify(itensVar)))
                    verificaSeTemTimeCadastrado()
                    setCadastrarTime(false)
                }
            })
                .catch((error) => {
                    alert('Erro ao cadastrar times')
                    setCarregando(false)
                });
            await apiC.post("torneio/cadastrarTimeSorteado", {
                "nome": enviarBanco[i],
            }).then(response => {
                if (response.status === 200) {
                    for (let i = 0; i < response.data.length; i++) {
                        times.push(response.data[i].nome)
                    }
                    setCarregando(false)
                    if (contador === i) {
                        let k = i
                        for (let j = 0; j < response.data.length; j++) {
                            itensVarSorteado[k] = response.data[j]
                            k++
                        }
                    }
                    setItensSorteados(JSON.parse(JSON.stringify(itensVarSorteado)))
                    buscarTimesSorteados()
                }

            })
                .catch((error) => {
                    alert('Erro ao cadastrar times na tabela de sorteados')
                    setCarregando(false)
                });
        }

    }


